const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const register = async (e) => {
    try {
      const user = await createUserWithEmailAndPassword();
    } catch (error) {
      console.log(error.message);
    }
  };

  const signIn = async (e) => {};

